import React, { useState } from "react";

function Userinput() {
  const [serviceList, setServiceList] = useState([{ service: "" }]);

  const handleServiceChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...serviceList];
    list[index][name] = value;
    setServiceList(list);
  };

  const handleServiceRemove = (index) => {
    const list = [...serviceList];
    list.splice(index, 1);
    setServiceList(list);
  };

  const handleServiceAdd = () => {
    setServiceList([...serviceList, { service: "" }]);
  };
  return (
    <div>
      <div class="form-row">
        <div class="form-group col-md-4">
          <label for="inputEmail4">Event Name</label>
          <input
            type="text"
            class="form-control"
            id="inputEmail4"
            placeholder="text"
          />
        </div>
      </div>
      <div class="form-row">
        <div class="form-group col-md-1">
          <label for="inputEmail4">No of Days</label>
          <input
            type="number"
            class="form-control"
            id="inputEmail4"
            placeholder="0"
          />
        </div>
      </div>
      <div class="form-group col-md-2 st_col">
        <label htmlfor="dt">Start date:</label>
        <input type="date" class="form-control" id="dt"></input>
      </div>
      <br />
      <form className="App" autoComplete="off">
        <div className="form-field">
          <label htmlFor="service">
            -------------------Activities--------------------
          </label>
          {serviceList.map((singleService, index) => (
            <div key={index} className="services">
              <div className="first-division">
                <div class="form-group col-md-2 st_col">
                  <label htmlFor="x">Name of the Activity </label>
                  <input
                    name="service"
                    type="text"
                    id="service"
                    value={singleService.service}
                    onChange={(e) => handleServiceChange(e, index)}
                    required
                  />
                </div>

                <div class="form-group col-md-2 st_col ">
                  <div>
                    <label for="time">Start time:</label>
                    <input type="time" class="form-control" id="time" />
                  </div>
                  <div>
                    <label for="time">Duration Time</label>
                    <input type="time" class="form-control" id="time" />
                  </div>
                </div>
                {serviceList.length - 1 === index && serviceList.length < 4 && (
                  <button
                    type="button"
                    onClick={handleServiceAdd}
                    className="add-btn"
                  >
                    <span>Add +</span>
                  </button>
                )}
              </div>
              <div className="second-division">
                {serviceList.length !== 1 && (
                  <button
                    type="button"
                    onClick={() => handleServiceRemove(index)}
                    className="remove-btn"
                  >
                    <span>Delete</span>
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </form>
      <br />
      <button>Submit</button>
    </div>
  );
}

export default Userinput;
