import React, { useState } from "react";
import { Activity } from "./activity";
import "./style.css";
function Userinput() {
  const [serviceList, setServiceList] = useState([]);
  // [
  //  {eventName: 'rishabh madharchod', noOfDays: 0, },
  //  {eventName: '', noOfDays: 0, },
  //  {eventName: '', noOfDays: 0, },
  // ]

  const handleServiceChange = (e, index) => {
    // console.log(e.target.timer);
    // console.log(e.target.value);
    // console.log(index);

    const { name, value } = e.target;
    const list = [...serviceList];
    list[index][name] = value;
    setServiceList(list);
    console.log(serviceList);
  };

  const handleServiceRemove = (e, index) => {
    e.preventDefault();
    console.log(index);
    let modArray = serviceList.filter((val, idx) => {
      console.log(idx);
      return idx != index;
    });
    console.log(modArray);
    // const list = [...serviceList];
    // list.splice(index, 1);
    setServiceList([...modArray]);
  };

  const handleServiceAdd = () => {
    setServiceList([...serviceList, { service: "" }]);
  };

  const myFunction = (e) => {
    e.preventDefault();
    window.location =
      "https://testbook.com/web-development/how-do-i-link-a-submit-button-to-another-webpage-using-html";
  };
  return (
    <div className="main">
      <h2>Schedule Creator</h2>
      <hr className="line" />
      <div className="form-row">
        <div className="form-group col-md-5 div2">
          <label htmlFor="inputEmail4">
            <strong>Event Name</strong>
          </label>
          <br />
          <input
            type="text"
            className="form-control"
            id="inputEmail4"
            placeholder="Enter event Name"
          />
        </div>
      </div>
      <div className="form-row">
        <div className="form-group col-md-2 div2">
          <label htmlFor="inputEmail4">
            <strong>No of Days</strong>
          </label>
          <input
            type="number"
            className="form-control"
            id="inputEmail4"
            placeholder="0"
          />
        </div>
      </div>
      <div className="form-group col-md-2 st_col">
        <label htmlFor="dt">
          <strong>Start date:</strong>
        </label>
        <input type="date" className="form-control" id="dt"></input>
      </div>
      <br />
      <h5> Add Your Activities...</h5>
      <form className="App" autoComplete="off">
        <div className="form-field">
          {serviceList.map((singleService, index) => (
            <Activity
              key={index}
              index={index}
              value={singleService.service}
              deleteActivity={handleServiceRemove}
              handleServiceChange={handleServiceChange}
            />
          ))}
          <button
            type="button"
            onClick={handleServiceAdd}
            className="buttonAdd"
          >
            Add
          </button>
        </div>
      </form>
      <br />
      <button
        type="button"
        class="btn btn-primary"
        data-toggle="modal"
        data-target="#exampleModal"
      >
        Launch demo modal
      </button>

      {/* <button className="btn btn-info submitBT" onClick={myFunction}>
        Submit
      </button> */}
    </div>
  );
}

export default Userinput;
