import React, { useState } from "react";
import { Activity } from "./activity";
import "./style.css";
function Userinput() {
  const [serviceList, setServiceList] = useState([]);

  const handleServiceChange = (e, index) => {
    console.log(e.target.timer);
    console.log(e.target.value);
    console.log(index);

    const { name, value } = e.target;
    const list = [...serviceList];
    list[index][name] = value;
    setServiceList(list);
  };

  const handleServiceRemove = (index) => {
    const list = [...serviceList];
    list.splice(index, 1);
    setServiceList(list);
  };

  const handleServiceAdd = () => {
    setServiceList([...serviceList, { service: "" }]);
  };

  const myFunction = (e) => {
    e.preventDefault();
    window.location =
      "https://testbook.com/web-development/how-do-i-link-a-submit-button-to-another-webpage-using-html";
  };
  return (
    <div className="main">
      <h2>Schedule Creator</h2>
      <hr className="line" />
      <div className="form-row">
        <div className="form-group col-md-7 div2">
          <label htmlFor="inputEmail4">
            <strong>Event Name</strong>
          </label>
          <br />
          <input
            type="text"
            className="form-control"
            id="inputEmail4"
            placeholder="Enter event Name"
          />
        </div>
      </div>
      <div className="form-row">
        <div className="form-group col-md-1">
          <label htmlFor="inputEmail4">No of Days</label>
          <input
            type="number"
            className="form-control"
            id="inputEmail4"
            placeholder="0"
          />
        </div>
      </div>
      <div className="form-group col-md-2 st_col">
        <label htmlFor="dt">Start date:</label>
        <input type="date" className="form-control" id="dt"></input>
      </div>
      <br />
      <form className="App" autoComplete="off">
        <div className="form-field">
          <label htmlFor="service">
            -------------------Activities--------------------
          </label>
          {serviceList.map((singleService, index) => (
            <Activity
              key={index}
              index={index}
              deleteActivity={handleServiceRemove}
              handleServiceChange={handleServiceChange}
            />
          ))}
          <button type="button" onClick={handleServiceAdd} className="add-btn">
            <span type="button" className="btn btn-success">
              Add +
            </span>
          </button>
        </div>
      </form>
      <br />
      <button className="btn btn-info" onClick={myFunction}>
        Submit
      </button>
    </div>
  );
}

export default Userinput;
